// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.15;

import "../node_modules/@openzeppelin/contracts/token/ERC20/extensions/ERC20Pausable.sol";
import "../node_modules/@openzeppelin/contracts/access/Ownable.sol";

//RNF01 - Os contratos devem seguir o padro do ERC20Pausable
contract SharedWalletContract is ERC20Pausable, Ownable {
    struct beneficiaryInfo {
        uint256 valueAllowed;
        bool isAllowed;
        uint256 lastDateWithdrawAllowance;
    }
    uint256 maxTokenAllowed = 1000000000000000000000000;
    mapping(address => beneficiaryInfo) public beneficiaries;

    //TASK 01 - Deve ser utilizado o modelo de tokens ERC20
    constructor() ERC20("Family Coins", "FCs") {
        /** @dev Dono dos tokens, geralmente  o endereo que executou o contrato, mais a quantidade de tokens que teremos em nossa moeda LBCs
         * A quantidade de tokens  10000, como precisamos converter wei para ether, multiplicamos a quantidade com 10 elevado a 18
         */

        addTokens(owner(), 10000 * 10**18);
    }

    /*
        RF01 - Deve ser possvel o cadastro dos endereos a serem beneficiados pela mesada. (Somente o admin poder realizar essa ao)
        RF02 - Deve ser possvel desabilitar o acesso de um beneficirio. (Somente o admin poder realizar essa ao)
        RF03 - Deve ser possvel estipular o valor que cada beneficirio poder retirar. (Somente o admin poder realizar essa ao) 
    */
    function setBeneficiary(
        address _beneficiary,
        bool _isAllowed,
        uint256 _amountAllowed
    ) public onlyOwner {
        beneficiaries[_beneficiary].valueAllowed = _amountAllowed;
        beneficiaries[_beneficiary].isAllowed = _isAllowed;
    }

    //RF05 - Deve ser possvel estipular o mximo de moedas que o nosso contrato pode ter. (Somente o admin poder realizar essa ao)
    function setMaxTokensAllowed(uint256 _maxTokenAllowed) public onlyOwner {
        maxTokenAllowed = _maxTokenAllowed;
    }

    //RF04 - Deve ser possvel adicionar moedas ao contrato. (Apenas administradores)
    function addTokens(address account, uint256 amount) public onlyOwner {
        require(
            balanceOf(owner()) + amount <= maxTokenAllowed,
            "This value exceeds the maximum number of tokens allowed"
        );

        _mint(account, amount);
    }

    //RF06 - Deve ser possvel pausar todas as transferncias.  (Somente o admin poder realizar essa ao)
    function pause() public onlyOwner {
        _pause();
    }

    //RF06 - Deve ser possvel pausar todas as transferncias.  (Somente o admin poder realizar essa ao)
    function unpause() public onlyOwner {
        _unpause();
    }

    /**
     * @dev See {IERC20-transfer}.
     *
     * Requirements:
     *
     * - `to` cannot be the zero address.
     * - the caller must have a balance of at least `amount`.
     */
    //RF07 - Deve ser possvel a retirada de moedas pelos beneficiados. (Apenas uma vez ao ms)
    function withdrawAllowance() public returns (bool) {
        require(
            block.timestamp >=
                (beneficiaries[msg.sender].lastDateWithdrawAllowance + 30 days),
            "You can only withdraw your allowance once every 30 days"
        );

        beneficiaries[msg.sender].lastDateWithdrawAllowance = block.timestamp;

        _transfer(owner(), msg.sender, beneficiaries[msg.sender].valueAllowed);
        return true;
    }
}
